#include <iostream>
#include <string>
#include <vector>

using namespace std;

int main() {
  int n;
  string s;
  cin >> n >> s;

  long long mod = 1e9 + 7;

  vector<vector<long long>> dp(n + 1, vector<long long>(n + 1, 0));
  dp[0][0] = 1;

  for (int i = 0; i <= n; ++i) {
    for (int j = 0; j <= i; ++j) {
      if (dp[i][j] == 0) continue;
      if (i + 1 <= n) {
        dp[i + 1][j + 1] = (dp[i + 1][j + 1] + dp[i][j]) % mod;
      }
      if (j > 0) {
        dp[i + 1][j - 1] = (dp[i + 1][j - 1] + dp[i][j]) % mod;
      }
    }
  }

  vector<vector<long long>> dp2(n + 1, vector<long long>(n + 1, 0));
  dp2[0][0] = 1;

  for (int i = 0; i < n; i++) {
    for (int j = 0; j <= i; j++) {
      if (dp2[i][j] == 0) continue;
      if (s[i] == '0') {
        dp2[i + 1][j + 1] = (dp2[i + 1][j + 1] + dp2[i][j]) % mod;
      } else {
        dp2[i+1][j+1] = (dp2[i+1][j+1] + dp2[i][j]) % mod;
      }
      if (j > 0) {
        dp2[i + 1][j - 1] = (dp2[i + 1][j - 1] + dp2[i][j]) % mod;
      }
    }
  }

  cout << dp2[n][s.length()] << endl;
  return 0;
}
