import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        String s = sc.next();
        int mod = 1000000007;
        int[][] dp = new int[n + 1][n + 1];
        dp[0][0] = 1;
        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= i; j++) {
                if (dp[i][j] == 0) continue;
                if (j < n) {
                    dp[i + 1][j + 1] = (dp[i + 1][j + 1] + dp[i][j]) % mod;
                }
                if (j > 0) {
                    dp[i + 1][j - 1] = (dp[i + 1][j - 1] + dp[i][j]) % mod;
                }
            }
        }
        int ans = 0;
        int len = s.length();
        for (int i = 0; i <= n; i++) {
            if (i + len > n) continue;
            long cur = dp[n][i];
            long ways = 1;
            for(int j=0; j<len; j++){
                if(s.charAt(j)=='0'){
                    ways = (ways * (i+j+1)) % mod;
                } else {
                    ways = (ways * (i+j+1)) % mod;
                }
            }
            ans = (ans + (int)ways) % mod;
        }
        System.out.println(ans);
    }
}
