#include <stdio.h>
#include <string.h>

int main() {
    char s[11];
    scanf("%s", s);
    char result[11] = "";
    int len = 0;
    for (int i = 0; i < strlen(s); i++) {
        if (s[i] == '0' || s[i] == '1') {
            result[len++] = s[i];
        } else if (s[i] == 'B' && len > 0) {
            len--;
        }
    }
    result[len] = '\0';
    printf("%s\n", result);
    return 0;
}
