#include <iostream>
#include <string>

using namespace std;

int main() {
  string s;
  cin >> s;
  string result = "";
  for (char c : s) {
    if (c == '0' || c == '1') {
      result += c;
    } else {
      if (!result.empty()) {
        result.pop_back();
      }
    }
  }
  cout << result << endl;
  return 0;
}
