import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        Arrays.sort(a);
        long minCost = Long.MAX_VALUE;
        for (int i = -100; i <= 100; i++) {
            long cost = 0;
            for (int j = 0; j < n; j++) {
                cost += (long) Math.pow(a[j] - i, 2);
            }
            minCost = Math.min(minCost, cost);
        }
        System.out.println(minCost);
    }
}
