#include <stdio.h>
#include <string.h>

int main() {
    char s[100001];
    scanf("%s", s);
    int n = strlen(s);
    for (int i = 0; i < n - 1; i++) {
        for (int j = i + 1; j < n; j++) {
            int len = j - i + 1;
            int count[26] = {0};
            for (int k = i; k <= j; k++) {
                count[s[k] - 'a']++;
            }
            int max_count = 0;
            for (int k = 0; k < 26; k++) {
                if (count[k] > max_count) {
                    max_count = count[k];
                }
            }
            if (max_count > len / 2) {
                printf("%d %d\n", i + 1, j + 1);
                return 0;
            }
        }
    }
    printf("-1 -1\n");
    return 0;
}
