#include <iostream>
#include <string>
#include <map>

using namespace std;

int main() {
  string s;
  cin >> s;

  for (int i = 0; i < s.length(); ++i) {
    for (int j = i + 1; j < s.length(); ++j) {
      string sub = s.substr(i, j - i + 1);
      map<char, int> counts;
      for (char c : sub) {
        counts[c]++;
      }

      int max_count = 0;
      for (auto const& [key, val] : counts) {
        max_count = max(max_count, val);
      }

      if (max_count > sub.length() / 2) {
        cout << i + 1 << " " << j + 1 << endl;
        return 0;
      }
    }
  }

  cout << -1 << " " << -1 << endl;
  return 0;
}
