#include <iostream>

using namespace std;

int main() {
  long long a, b, c, d, e, f;
  cin >> a >> b >> c >> d >> e >> f;

  long long max_sugar_water = 0;
  long long max_sugar = 0;

  for (long long i = 0; i <= 30; ++i) {
    for (long long j = 0; j <= 30; ++j) {
      long long water = 100 * (a * i + b * j);
      for (long long k = 0; k <= 30; ++k) {
        for (long long l = 0; l <= 30; ++l) {
          long long sugar = c * k + d * l;
          if (sugar * 100 <= water * e && water + sugar <= f) {
            if (water + sugar > max_sugar_water) {
              max_sugar_water = water + sugar;
              max_sugar = sugar;
            }
          }
        }
      }
    }
  }
  cout << max_sugar_water << " " << max_sugar << endl;
  return 0;
}
