import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long a = sc.nextLong();
        long b = sc.nextLong();

        if (a == 0 || b == 0) {
            System.out.println("Zero");
        } else if (b - a + 1 >= 0) {
            if ((b < 0 && a < 0 && (b-a+1) % 2 != 0) || (a < 0 && b > 0)) {
                System.out.println("Negative");
            } else {
                System.out.println("Positive");
            }
        } else {
            System.out.println("Zero");
        }
    }
}
