#include <stdio.h>
#include <stdbool.h>

int main() {
    int n, m;
    scanf("%d %d", &n, &m);
    int x[m], y[m];
    for (int i = 0; i < m; i++) {
        scanf("%d %d", &x[i], &y[i]);
    }
    bool possible[n + 1];
    for (int i = 1; i <= n; i++) {
        possible[i] = false;
    }
    possible[1] = true;
    for (int i = 0; i < m; i++) {
        bool temp[n + 1];
        for (int j = 1; j <= n; j++) {
            temp[j] = false;
        }
        for (int j = 1; j <= n; j++) {
            if (possible[j]) {
                if (j == x[i]) {
                    temp[y[i]] = true;
                } else {
                    temp[j] = true;
                }
            }
        }
        for (int j = 1; j <= n; j++) {
            possible[j] = temp[j];
        }

    }
    int count = 0;
    for (int i = 1; i <= n; i++) {
        if (possible[i]) {
            count++;
        }
    }
    printf("%d\n", count);
    return 0;
}
