import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        Set<Integer> possibleBoxes = new HashSet<>();
        possibleBoxes.add(1);
        for (int i = 0; i < m; i++) {
            int x = sc.nextInt();
            int y = sc.nextInt();
            Set<Integer> nextPossibleBoxes = new HashSet<>();
            for (int box : possibleBoxes) {
                if (box == x) {
                    nextPossibleBoxes.add(y);
                } else {
                    nextPossibleBoxes.add(box);
                }
            }
            possibleBoxes = nextPossibleBoxes;
        }
        System.out.println(possibleBoxes.size());
    }
}
