def solve():
    n, m = map(int, input().split())
    edges = []
    for _ in range(m):
        u, v = map(int, input().split())
        edges.append((u, v))

    possible_locations = {1}

    for i in range(m):
        next_possible_locations = set()
        for loc in possible_locations:
            u, v = edges[i]
            if loc == u:
                next_possible_locations.add(v)
            else:
                next_possible_locations.add(loc)
        possible_locations = next_possible_locations

    print(len(possible_locations))

