    for (int i = 0; i < N; i++) {
        scanf("%lld", &a[i]);
        sum += a[i];
    }
    long long left[N], right[N];
    left[0] = a[0];
    right[N - 1] = a[N - 1];
    for (int i = 1; i < N; i++) {
        left[i] = left[i - 1] + a[i];
    }
    for (int i = N - 2; i >= 0; i--) {
        right[i] = right[i + 1] + a[i];
    }
    if (sum < L) {
        printf("Impossible\n");
        return 0;
    }
