import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long l = sc.nextInt();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        List<Integer> ans = new ArrayList<>();
        long sum = 0;
        for (int i = 0; i < n - 1; i++) {
            sum += a[i];
        }
        long cur = sum;
        for (int i = 0; i < n - 1; i++) {
            if (cur >= l) {
                ans.add(i + 1);
                cur -= a[i];
            } else {
                sum = 0;
                for (int j = n - 1; j > i; j--) {
                    sum += a[j];
                }
                cur = sum + a[i];
                if (cur < l) {
                    System.out.println("Impossible");
                    return;
                } else {
                    ans.add(i + 1);
                    cur -= a[i];
                }
            }

        }
        System.out.println("Possible");
        for (int i = 0; i < ans.size(); i++) {
            System.out.println(ans.get(i));
        }
    }
}
