import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int A = sc.nextInt();
        int B = sc.nextInt();
        int C = sc.nextInt();
        int D = sc.nextInt();
        int E = sc.nextInt();
        int F = sc.nextInt();
        int maxSugar = 0;
        int maxWater = 0;
        for (int i = 0; i <= 3000 / (100 * A); i++) {
            for (int j = 0; j <= 3000 / (100 * B); j++) {
                int water = 100 * A * i + 100 * B * j;
                int sugarCapacity = water * E / 100;
                for (int k = 0; k <= 3000 / C; k++) {
                    for (int l = 0; l <= 3000 / D; l++) {
                        int sugar = C * k + D * l;
                        if (sugar <= sugarCapacity && water + sugar <= F) {
                            if (sugar > maxSugar) {
                                maxSugar = sugar;
                                maxWater = water;
                            }
                        }
                    }
                }
            }
        }
        System.out.println((maxWater + maxSugar) + " " + maxSugar);
        sc.close();
    }
}
