import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        List<int[]> edges = new ArrayList<>();
        for (int i = 0; i < m; i++) {
            edges.add(new int[]{sc.nextInt(), sc.nextInt()});
        }
        int q = sc.nextInt();
        for (int i = 0; i < q; i++) {
            int x = sc.nextInt();
            int y = sc.nextInt();
            int z = sc.nextInt();
            int minScore = Integer.MAX_VALUE;
            for (int j = 0; j < (1 << m); j++) {
                int score = 0;
                Set<Integer> visited = new HashSet<>();
                Queue<Integer> q1 = new LinkedList<>();
                Queue<Integer> q2 = new LinkedList<>();
                q1.offer(x);
                q2.offer(y);
                visited.add(x);
                visited.add(y);
                int count = 2;
                List<Integer> usedEdges = new ArrayList<>();
                for (int k = 0; k < m; k++) {
                    if ((j >> k) % 2 == 1) {
                        usedEdges.add(k);
                    }
                }
                boolean possible = true;
                while (!q1.isEmpty() || !q2.isEmpty() && count < z) {
                    int cur = -1;
                    if (!q1.isEmpty()) {
                        cur = q1.poll();
                    } else {
                        cur = q2.poll();
                    }
                    for (int k = 0; k < usedEdges.size(); k++) {
                        int edgeIndex = usedEdges.get(k);
                        int u = edges.get(edgeIndex)[0];
                        int v = edges.get(edgeIndex)[1];
                        if ((u == cur && !visited.contains(v)) || (v == cur && !visited.contains(u))) {
                            int next = (u == cur) ? v : u;
                            q1.offer(next);
                            q2.offer(next);
                            visited.add(next);
                            count++;
                            score = Math.max(score, edgeIndex + 1);
                            break;
                        }
                    }
                    if(count < z && (q1.isEmpty() && q2.isEmpty())){
                        possible = false;
                        break;
                    }
                }
                if (count == z && possible) {
                    minScore = Math.min(minScore, score);
                }
            }
            System.out.println(minScore);
        }
    }
}
