n = int(input())
a = list(map(int, input().split()))

if max(a) == 1 and n % 2 == 1:
    print("Second")
elif max(a) == 1 and n % 2 == 0:
    print("First")
elif max(a) == min(a):
    if n % 2 == 0:
        print("Second")
    else:
        print("First")

else:

    nim_sum = 0
    for x in a:
        nim_sum ^= x

    if nim_sum == 0:
        print("Second")
    else:
        print("First")
