import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        long mod = (long) Math.pow(10, 9) + 7;

        BigInteger ans = BigInteger.ONE;
        BigInteger nk = BigInteger.valueOf(n * k);
        BigInteger n_fact = factorial(n, mod);
        BigInteger nk_fact = factorial(n * k, mod);
        BigInteger k_fact = factorial(k, mod);
        BigInteger k_fact_n = k_fact.pow(n);


        ans = nk_fact.multiply(n_fact.modInverse(BigInteger.valueOf(mod)));
        ans = ans.multiply(k_fact_n.modInverse(BigInteger.valueOf(mod)));

        System.out.println(ans.mod(BigInteger.valueOf(mod)));
    }

    public static BigInteger factorial(int n, long mod) {
        BigInteger fact = BigInteger.ONE;
        for (int i = 2; i <= n; i++) {
            fact = fact.multiply(BigInteger.valueOf(i)).mod(BigInteger.valueOf(mod));
        }
        return fact;
    }
}
