MOD = 10**9 + 7

def solve():
    n, k = map(int, input().split())

    fact = [1] * (n * k + 1)
    for i in range(2, n * k + 1):
        fact[i] = (fact[i-1] * i) % MOD

    def pow_mod(a, b, m):
        res = 1
        a %= m
        while b > 0:
            if b % 2 == 1:
                res = (res * a) % m
            a = (a * a) % m
            b //= 2
        return res

    def inv_mod(a, m):
        return pow_mod(a, m - 2, m)

    def comb_mod(n, k, m):
        if k < 0 or k > n:
            return 0
        num = fact[n]
        den = (fact[k] * fact[n-k]) % m
        return (num * inv_mod(den, m)) % m

    ans = (fact[n*k] * inv_mod(fact[k]**n, MOD)) % MOD

    print(ans)

solve()
