#include <stdio.h>
#include <stdbool.h>

bool containsDislikedDigit(int num, int k, int dislikedDigits[]) {
    int temp = num;
    while (temp > 0) {
        int digit = temp % 10;
        for (int i = 0; i < k; i++) {
            if (digit == dislikedDigits[i]) {
                return true;
            }
        }
        temp /= 10;
    }
    return false;
}

int main() {
    int n, k;
    scanf("%d %d", &n, &k);
    int dislikedDigits[k];
    for (int i = 0; i < k; i++) {
        scanf("%d", &dislikedDigits[i]);
    }
    int amount = n;
    while (containsDislikedDigit(amount, k, dislikedDigits)) {
        amount++;
    }
    printf("%d\n", amount);
    return 0;
}
