#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

bool containsDislikedDigit(long long n, const vector<int>& dislikedDigits) {
  string s = to_string(n);
  for (char c : s) {
    int digit = c - '0';
    if (find(dislikedDigits.begin(), dislikedDigits.end(), digit) != dislikedDigits.end()) {
      return true;
    }
  }
  return false;
}

int main() {
  long long n;
  int k;
  cin >> n >> k;

  vector<int> dislikedDigits(k);
  for (int i = 0; i < k; ++i) {
    cin >> dislikedDigits[i];
  }

  long long result = n;
  while (containsDislikedDigit(result, dislikedDigits)) {
    result++;
  }

  cout << result << endl;
  return 0;
}
