#include <stdio.h>

#define MOD 1000000007

long long combinations(int n, int k, long long fact[], long long inv[]) {
    if (k < 0 || k > n) return 0;
    return fact[n] * inv[k] % MOD * inv[n - k] % MOD;
}

long long power(long long base, long long exp) {
    long long res = 1;
    base %= MOD;
    while (exp > 0) {
        if (exp % 2 == 1) res = (res * base) % MOD;
        base = (base * base) % MOD;
        exp /= 2;
    }
    return res;
}

long long inverse(long long n) {
    return power(n, MOD - 2);
}

int main() {
    int H, W, A, B;
    scanf("%d %d %d %d", &H, &W, &A, &B);

    long long fact[H + W + 1];
    long long inv[H + W + 1];
    fact[0] = 1;
    inv[0] = 1;
    for (int i = 1; i <= H + W; i++) {
        fact[i] = (fact[i - 1] * i) % MOD;
        inv[i] = inverse(fact[i]);
    }

    long long total_paths = combinations(H + W - 2, H - 1, fact, inv);
    long long forbidden_paths = combinations(H - 1 + W -1 -A-B , H -A-1, fact, inv);
    long long result = (total_paths - forbidden_paths + MOD) % MOD;

    printf("%lld\n", result);
    return 0;
}
