#include <iostream>
#include <vector>

using namespace std;

long long combinations(int n, int k, const vector<long long>& fact, const vector<long long>& inv) {
  if (k < 0 || k > n) return 0;
  return fact[n] * inv[k] % 1000000007 * inv[n - k] % 1000000007;
}

long long power(long long base, long long exp) {
  long long res = 1;
  base %= 1000000007;
  while (exp > 0) {
    if (exp % 2 == 1) res = (res * base) % 1000000007;
    base = (base * base) % 1000000007;
    exp /= 2;
  }
  return res;
}

long long inverse(long long n) {
  return power(n, 1000000007 - 2);
}

int main() {
  long long h, w, a, b;
  cin >> h >> w >> a >> b;

  vector<long long> fact(h + w + 1);
  vector<long long> inv(h + w + 1);
  fact[0] = 1;
  inv[0] = 1;
  for (long long i = 1; i <= h + w; ++i) {
    fact[i] = (fact[i - 1] * i) % 1000000007;
    inv[i] = inverse(fact[i]);
  }

  long long total_paths = combinations(h + w - 2, h - 1, fact, inv);
  long long forbidden_paths = combinations(h - a + w- b-2, h-a-1, fact, inv);

  cout << (total_paths - forbidden_paths + 1000000007) % 1000000007 << endl;

  return 0;
}
