#include <stdio.h>

#define MOD 1000000007

int main() {
    long long n, x, y, z;
    scanf("%lld %lld %lld %lld", &n, &x, &y, &z);

    long long count = 0;
    long long sequence[40];

    if (n < 3) {
        printf("0\n");
        return 0;
    }


    for (long long i = 0; i < (long long)pow(10, n); ++i) {
        long long temp = i;
        long long sum = 0;
        long long haiku_found = 0;

        for (long long j = 0; j < n; ++j) {
            sequence[j] = (temp % 10) + 1;
            temp /= 10;
        }


        for (long long j = 0; j < n; ++j) {
            sum = 0;
            for (long long k = j; k < n; ++k) {
                sum += sequence[k];
                if (sum == x) {
                    long long sum2 = 0;
                    for (long long l = k + 1; l < n; ++l) {
                        sum2 += sequence[l];
                        if (sum2 == y) {
                            long long sum3 = 0;
                            for (long long m = l + 1; m < n; ++m) {
                                sum3 += sequence[m];
                                if (sum3 == z) {
                                    haiku_found = 1;
                                    break;
                                }
                            }
                            if(haiku_found) break;
                        }
                    }
                    if(haiku_found) break;
                }
            }
            if(haiku_found) break;
        }
        if (haiku_found) {
            count++;
        }
    }

    printf("%lld\n", count % MOD);

    return 0;
}
