#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, x, y, z;
  cin >> n >> x >> y >> z;

  long long mod = 1e9 + 7;
  long long count = 0;

  vector<long long> a(n);
  function<void(long long)> generate = [&](long long k) {
    if (k == n) {
      for (long long i = 0; i < n; ++i) {
        for (long long j = i + 1; j < n; ++j) {
          for (long long l = j + 1; l < n; ++l) {
            long long sum1 = 0;
            for (long long m = i; m < j; ++m) {
              sum1 += a[m];
            }
            if (sum1 == x) {
              long long sum2 = 0;
              for (long long m = j; m < l; ++m) {
                sum2 += a[m];
              }
              if (sum2 == y) {
                long long sum3 = 0;
                for (long long m = l; m < n; ++m) {
                  sum3 += a[m];
                }
                if (sum3 == z) {
                  count = (count + 1) % mod;
                  return;
                }
              }
            }
          }
        }
      }
      return;
    }
    for (long long i = 1; i <= 10; ++i) {
      a[k] = i;
      generate(k + 1);
    }
  };

  generate(0);
  cout << count << endl;
  return 0;
}
