import java.util.*;
import java.lang.*;

public class Main {
    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int x = sc.nextInt();
        int y = sc.nextInt();
        int z = sc.nextInt();

        long count = 0;
        long total = (long) Math.pow(10, n);

        for (long i = 0; i < total; i++) {
            long temp = i;
            int[] arr = new int[n];
            for (int j = n - 1; j >= 0; j--) {
                arr[j] = (int) (temp % 10);
                temp /= 10;
            }

            boolean haikuFound = false;
            for (int a = 0; a < n; a++) {
                for (int b = a + 1; b < n; b++) {
                    for (int c = b + 1; c < n; c++) {
                        int sum1 = 0;
                        for (int k = a; k < b; k++) {
                            sum1 += arr[k];
                        }
                        int sum2 = 0;
                        for (int k = b; k < c; k++) {
                            sum2 += arr[k];
                        }
                        int sum3 = 0;
                        for (int k = c; k < n; k++) {
                            sum3 += arr[k];
                        }
                        if (sum1 == x && sum2 == y && sum3 == z) {
                            haikuFound = true;
                            break;
                        }
                    }
                    if (haikuFound) break;
                }
                if (haikuFound) break;
            }
            if (haikuFound) count++;
        }
        System.out.println(count % MOD);
    }
}
