MOD = 10**9 + 7

def solve():
    n, x, y, z = map(int, input().split())

    ans = 0

    def count_haiku(seq):
        for i in range(len(seq)):
            for j in range(i + 1, len(seq)):
                for k in range(j + 1, len(seq)):
                    for l in range(k + 1, len(seq) + 1):
                        sum1 = sum(seq[i:j])
                        sum2 = sum(seq[j:k])
                        sum3 = sum(seq[k:l])
                        if sum1 == x and sum2 == y and sum3 == z:
                            return 1
        return 0

    def generate_sequences(length, current_seq):
        nonlocal ans
        if length == 0:
            ans = (ans + count_haiku(current_seq)) % MOD
            return

        for i in range(1, 11):
            generate_sequences(length - 1, current_seq + [i])

    generate_sequences(n, [])
    print(ans)

solve()
