#include <stdio.h>
#include <stdlib.h>

long long int main() {
    int n;
    scanf("%d", &n);
    long long int a[n][n];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            scanf("%lld", &a[i][j]);
        }
    }
    long long int ans = 0;
    long long int edges[n][n];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            edges[i][j] = 0;
        }
    }
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            long long int min = a[i][j];
            for (int k = 0; k < n; k++) {
                if (k != i && k != j) {
                    if (a[i][k] + a[k][j] < min) {
                        min = a[i][k] + a[k][j];
                    }
                }
            }
            if (min < a[i][j]) {
                printf("-1\n");
                return 0;
            }
            if (a[i][j] != min)
                edges[i][j] = edges[j][i] = a[i][j];

        }
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            ans += edges[i][j];
        }
    }
    printf("%lld\n", ans);
    return 0;
}
