#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main() {
    int n, k;
    scanf("%d %d", &n, &k);
    char **s = (char **)malloc(n * sizeof(char *));
    int len[n];
    for (int i = 0; i < n; i++) {
        s[i] = (char *)malloc(k * sizeof(char));
        scanf("%s", s[i]);
        len[i] = strlen(s[i]);
    }
    char *ans = (char *)malloc((k + 1) * sizeof(char));
    strcpy(ans, "");
    int dp[n + 1][k + 1];
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= k; j++) {
            dp[i][j] = -1;
        }
    }
    dp[0][0] = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j <= k; j++) {
            if (dp[i][j] != -1) {
                if (j + len[i] <= k) {
                    if (dp[i + 1][j + len[i]] == -1 || strcmp(ans, strcat(ans, s[i])) < 0) {
                        strcpy(ans, "");
                        strcat(ans, s[i]);
                        dp[i + 1][j + len[i]] = 1;
                    }
                }
            }
        }
    }
    char *res = (char *)malloc((k + 1) * sizeof(char));
    strcpy(res, "");
    int cur = k;
    for (int i = n; i > 0; i--) {
        if (dp[i][cur] == 1) {
            strcat(res, s[i - 1]);
            cur -= len[i - 1];
        }
    }
    printf("%s\n", res);
    for (int i = 0; i < n; i++) {
        free(s[i]);
    }
    free(s);
    free(ans);
    free(res);
    return 0;
}
