#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int main() {
  int n, k;
  cin >> n >> k;

  vector<string> s(n);
  for (int i = 0; i < n; ++i) {
    cin >> s[i];
  }

  string best_s = "";
  for (int i = 0; i < (1 << n); ++i) {
    string current_s = "";
    int current_len = 0;
    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        current_s += s[j];
        current_len += s[j].length();
      }
    }
    if (current_len == k) {
      if (best_s == "" || current_s < best_s) {
        best_s = current_s;
      }
    }
  }
  cout << best_s << endl;
  return 0;
}
