import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        String[] s = new String[n];
        for (int i = 0; i < n; i++) {
            s[i] = sc.next();
        }
        String ans = "";
        for (int i = 0; i < (1 << n); i++) {
            String cur = "";
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    cur += s[j];
                }
            }
            if (cur.length() == k) {
                if (ans.equals("") || cur.compareTo(ans) < 0) {
                    ans = cur;
                }
            }
        }
        System.out.println(ans);
    }
}
