#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  int n, l;
  cin >> n >> l;

  vector<string> s(n);
  for (int i = 0; i < n; ++i) {
    cin >> s[i];
  }

  sort(s.begin(), s.end());

  string result = "";
  for (int i = 0; i < n; ++i) {
    result += s[i];
  }

  cout << result << endl;
  return 0;
}
