#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<vector<long long>> a(n, vector<long long>(n));
  for (int i = 0; i < n; ++i) {
    for (int j = 0; j < n; ++j) {
      cin >> a[i][j];
    }
  }

  long long total_length = 0;
  vector<tuple<int, int, long long>> edges;

  for (int i = 0; i < n; ++i) {
    for (int j = i + 1; j < n; ++j) {
      bool found_shorter = false;
      for (int k = 0; k < n; ++k) {
        if (k != i && k != j && a[i][j] > a[i][k] + a[k][j]) {
          found_shorter = true;
          break;
        }
      }
      if (found_shorter) {
        cout << -1 << endl;
        return 0;
      }
      edges.emplace_back(i, j, a[i][j]);
      total_length += a[i][j];

    }
  }
  cout << total_length << endl;
  return 0;
}
