#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

bool containsDislikedDigit(int n, const vector<int>& dislikedDigits) {
    string s = to_string(n);
    for (char c : s) {
        int digit = c - '0';
        if (find(dislikedDigits.begin(), dislikedDigits.end(), digit) != dislikedDigits.end()) {
            return true;
        }
    }
    return false;
}

int main() {
    int n, k;
    cin >> n >> k;

    vector<int> dislikedDigits(k);
    for (int i = 0; i < k; ++i) {
        cin >> dislikedDigits[i];
    }

    for (int i = n; ; ++i) {
        if (!containsDislikedDigit(i, dislikedDigits)) {
            cout << i << endl;
            break;
        }
    }

    return 0;
}
