import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        boolean[] disliked = new boolean[10];
        for (int i = 0; i < k; i++) {
            disliked[sc.nextInt()] = true;
        }
        while (true) {
            boolean ok = true;
            int temp = n;
            while (temp > 0) {
                if (disliked[temp % 10]) {
                    ok = false;
                    break;
                }
                temp /= 10;
            }
            if (ok) {
                System.out.println(n);
                break;
            }
            n++;
        }
    }
}
