import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long h = sc.nextLong();
        long w = sc.nextLong();
        long a = sc.nextLong();
        long b = sc.nextLong();
        long mod = 1000000007;

        long[][] dp = new long[(int)h][(int)w];
        dp[0][0] = 1;

        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                if (i + a >= h && j + b >= w) continue;
                if (i + 1 < h && j + b < w) {
                    if(i + 1 >= h -a && j < b) continue;
                    dp[i + 1][j] = (dp[i + 1][j] + dp[i][j]) % mod;
                }
                if (i + a < h && j + 1 < w) {
                    if(i < h -a && j+1 >= w -b) continue;
                    dp[i][j + 1] = (dp[i][j + 1] + dp[i][j]) % mod;
                }
            }
        }
        System.out.println(dp[h - 1][w - 1]);
    }
}
