#include <stdio.h>
#include <stdlib.h>

int compare(const void *a, const void *b) {
    return *(int *)a - *(int *)b;
}

int main() {
    int n;
    scanf("%d", &n);
    int l[2 * n];
    for (int i = 0; i < 2 * n; i++) {
        scanf("%d", &l[i]);
    }
    qsort(l, 2 * n, sizeof(int), compare);
    long long sum = 0;
    for (int i = 0; i < n; i++) {
        sum += l[i];
    }
    printf("%lld\n", sum);
    return 0;
}
