#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> l(2 * n);
  for (int i = 0; i < 2 * n; ++i) {
    cin >> l[i];
  }

  sort(l.begin(), l.end());

  long long total_ingredients = 0;
  for (int i = 0; i < n; ++i) {
    total_ingredients += l[i];
  }

  cout << total_ingredients << endl;
  return 0;
}
