import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[][] a = new long[n][n];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                a[i][j] = sc.nextLong();
            }
        }
        long ans = 0;
        List<long[]> edges = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                edges.add(new long[]{i, j, a[i][j]});
            }
        }
        Collections.sort(edges, (x, y) -> Long.compare(x[2], y[2]));
        long[] minEdge = new long[n];
        Arrays.fill(minEdge, Long.MAX_VALUE);
        for (long[] edge : edges) {
            long u = edge[0];
            long v = edge[1];
            long w = edge[2];
            if (w < minEdge[u] && w < minEdge[v]) {
                ans += w;
                minEdge[u] = w;
                minEdge[v] = w;
            }
        }
        long[][] dist = new long[n][n];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                dist[i][j] = a[i][j];
            }
        }
        for (long[] edge : edges) {
            long u = edge[0];
            long v = edge[1];
            long w = edge[2];
            if (w < minEdge[u] || w < minEdge[v]) continue;
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    dist[i][j] = Math.min(dist[i][j], dist[i][u] + w + dist[v][j]);
                    dist[i][j] = Math.min(dist[i][j], dist[i][v] + w + dist[u][j]);
                }
            }
        }
        boolean ok = true;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (dist[i][j] != a[i][j]) {
                    ok = false;
                    break;
                }
            }
            if (!ok) break;
        }
        if (ok) System.out.println(ans);
        else System.out.println(-1);
    }
}
