#include <stdio.h>

int main() {
    long long N, X;
    scanf("%lld %lld", &N, &X);
    long long ans = 0;
    long long cur_x = X;
    long long cur_y = 0;
    long long dx = 1;
    long long dy = 0;
    while (1) {
        long long next_x = cur_x + dx;
        long long next_y = cur_y + dy;
        if (next_x < 0 || next_x > N || next_y < 0 || next_y > N) {
            if (next_x < 0) {
                next_x = -next_x;
                dx = -dx;
            }
            if (next_x > N) {
                next_x = 2 * N - next_x;
                dx = -dx;
            }
            if (next_y < 0) {
                next_y = -next_y;
                dy = -dy;
            }
            if (next_y > N) {
                next_y = 2 * N - next_y;
                dy = -dy;
            }
        }

        long long dist = (long long)((next_x - cur_x) * (next_x - cur_x) + (next_y - cur_y) * (next_y - cur_y));

        ans += (long long)sqrt(dist);
        cur_x = next_x;
        cur_y = next_y;
        if(cur_x == X && cur_y == 0) break;
    }
    printf("%lld\n", ans);
    return 0;
}
