#include <iostream>

int main() {
    long long N, X;
    std::cin >> N >> X;
    long long ans = 0;
    long long curX = X;
    long long curY = 0;
    while (true) {
        long long dx = 0, dy = 0;
        if (curX + curY <= N) {
            dx = curY;
            dy = curX;
        } else if (curX + curY <= 2 * N) {
            dx = 2 * N - curX - curY;
            dy = N - curX;
        } else {
            dx = curX + curY - 2 * N;
            dy = 2 * N - curX;
        }

        ans += std::sqrt(dx * dx + dy * dy);
        curX += dx;
        curY += dy;
        if (curX == X && curY == 0) break;

    }
    std::cout << ans << std::endl;
    return 0;
}
