import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long x = sc.nextLong();
        long ans = 0;
        long curX = x;
        long curY = 0;
        long dx = 1;
        long dy = 0;
        while (true) {
            long nextX = curX + dx;
            long nextY = curY + dy;
            if (nextX < 0 || nextX > n) {
                dx *= -1;
                nextX = curX + dx;
                nextY = curY + dy;
            }
            if (nextY < 0 || nextY > n * Math.sqrt(3) / 2) {
                dy *= -1;
                nextX = curX + dx;
                nextY = curY + dy;

            }

            double dist = Math.sqrt(Math.pow(nextX-curX,2)+Math.pow(nextY-curY,2));
            ans += dist;
            if(nextX == x && nextY == 0 && dx == 1 && dy ==0) break;
            curX = nextX;
            curY = nextY;
            if(curX == x && curY == 0 && dx == -1 && dy == 0) break;

            if (curX == x && curY == 0) break;

        }
        System.out.println(ans);
        sc.close();

    }
}
