#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define MAXN 2005
#define MAXE 4005

int n, k;
int head[MAXN], nxt[MAXE], to[MAXE], edge_cnt;
int dist[MAXN][MAXN];

void addedge(int u, int v) {
    nxt[edge_cnt] = head[u];
    to[edge_cnt] = v;
    head[u] = edge_cnt++;
    nxt[edge_cnt] = head[v];
    to[edge_cnt] = u;
    head[v] = edge_cnt++;
}

void bfs(int s, int d[]) {
    for (int i = 1; i <= n; i++) d[i] = -1;
    d[s] = 0;
    int q[MAXN], head_q = 0, tail_q = 0;
    q[tail_q++] = s;
    while (head_q < tail_q) {
        int u = q[head_q++];
        for (int i = head[u]; i != -1; i = nxt[i]) {
            int v = to[i];
            if (d[v] == -1) {
                d[v] = d[u] + 1;
                q[tail_q++] = v;
            }
        }
    }
}

int main() {
    scanf("%d %d", &n, &k);
    edge_cnt = 0;
    for (int i = 1; i <= n; i++) head[i] = -1;
    for (int i = 0; i < n - 1; i++) {
        int u, v;
        scanf("%d %d", &u, &v);
        addedge(u, v);
    }
    for (int i = 1; i <= n; i++) {
        bfs(i, dist[i]);
    }
    int min_removed = n;
    for (int i = 0; i < (1 << n); i++) {
        int removed = 0;
        int nodes[MAXN], node_cnt = 0;
        for (int j = 0; j < n; j++) {
            if (!((i >> j) & 1)) {
                nodes[node_cnt++] = j + 1;
            } else {
                removed++;
            }
        }
        if (node_cnt == 0) continue;
        int max_dist = 0;
        for (int j = 0; j < node_cnt; j++) {
            for (int l = j + 1; l < node_cnt; l++) {
                int d = INT_MAX;

                int q[MAXN], head_q = 0, tail_q = 0, visited[MAXN] = {0};
                q[tail_q++] = nodes[j];
                visited[nodes[j]] = 1;
                int cur_d = 0;
                int found = 0;
                while(head_q < tail_q){
                    int u = q[head_q++];
                    if(u == nodes[l]){
                        d = cur_d;
                        found = 1;
                        break;
                    }
                    for(int e = head[u]; e != -1; e = nxt[e]){
                        int v = to[e];
                        if(visited[v] == 0 && ((i >> (v-1)) & 1) == 0){
                            visited[v] = 1;
                            q[tail_q++] = v;
                        }
                    }
                    cur_d++;
                }
                if(found)
                    max_dist = (max_dist > d) ? max_dist : d;
            }
        }
        if (max_dist <= k) {
            min_removed = (min_removed < removed) ? min_removed : removed;
        }
    }
    printf("%d\n", min_removed);
    return 0;
}
