import itertools

def solve():
    n, k = map(int, input().split())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u - 1, v - 1))

    min_removed = float('inf')

    for i in range(1 << n):
        nodes = []
        for j in range(n):
            if (i >> j) & 1:
                nodes.append(j)

        if not nodes:
            continue

        adj = [[] for _ in range(n)]
        for u, v in edges:
            if u in nodes and v in nodes:
                adj[u].append(v)
                adj[v].append(u)

        if not is_connected(adj, nodes):
            continue


        max_dist = 0
        for u in nodes:
            for v in nodes:
                max_dist = max(max_dist, bfs(adj, u, nodes,v))

        if max_dist <= k:
            min_removed = min(min_removed, n - len(nodes))

    print(min_removed)

def is_connected(adj, nodes):
    visited = [False] * len(adj)
    q = [nodes[0]]
    visited[nodes[0]] = True
    count = 0
    while q:
        u = q.pop(0)
        count+=1
        for v in adj[u]:
            if not visited[v]:
                visited[v] = True
                q.append(v)
    return count == len(nodes)

def bfs(adj, start_node, nodes, end_node):
    q = [(start_node, 0)]
    visited = {start_node}
    while q:
        curr, dist = q.pop(0)
        if curr == end_node:
            return dist
        for neighbor in adj[curr]:
            if neighbor not in visited and neighbor in nodes:
                visited.add(neighbor)
                q.append((neighbor, dist + 1))
    return -1

solve()
