#include <stdio.h>

int main() {
    int N, M;
    scanf("%d %d", &N, &M);
    int A[M];
    int sum = 0;
    for (int i = 0; i < M; i++) {
        scanf("%d", &A[i]);
        sum += A[i];
    }
    if (sum != N) {
        printf("Impossible\n");
        return 0;
    }
    if (M == 1) {
        printf("%d\n", N);
        printf("%d\n", N / 2);
        for (int i = 1; i <= N / 2; i++) {
            printf("%d ", i);
        }
        printf("\n");

    } else if (M == 2 && A[0] == 1 && A[1] == 2) {
        printf("1 2\n");
        printf("1\n");
        printf("3\n");
    }
    else {
        printf("Impossible\n");
    }
    return 0;
}
