import itertools

n = int(input())
a = []
for _ in range(n):
    a.append(list(map(int, input().split())))

edges = []
for i in range(n):
    for j in range(i + 1, n):
        edges.append((i, j, a[i][j]))

min_total_length = float('inf')
for i in range(1 << len(edges)):
    total_length = 0
    selected_edges = []
    for j in range(len(edges)):
        if (i >> j) & 1:
            total_length += edges[j][2]
            selected_edges.append(edges[j])

    dist = [[float('inf')] * n for _ in range(n)]
    for k in range(n):
        dist[k][k] = 0
    for u, v, w in selected_edges:
        dist[u][v] = dist[v][u] = w

    for k in range(n):
        for i in range(n):
            for j in range(n):
                dist[i][j] = min(dist[i][j], dist[i][k] + dist[k][j])

    valid = True
    for u in range(n):
        for v in range(n):
            if dist[u][v] != a[u][v]:
                valid = False
                break
        if not valid:
            break

    if valid:
        min_total_length = min(min_total_length, total_length)

if min_total_length == float('inf'):
    print(-1)
else:
    print(min_total_length)

