    for (int i = 0; i < m; ++i) {
        cin >> a[i];
    }

    if (m == 1) {
        cout << n << endl;
        cout << n / 2 + (n % 2) << endl;
        for (int i = 1; i <= n / 2 + (n % 2); ++i) {
            cout << i << (i == n / 2 + (n % 2) ? "" : " ");
        }
        cout << endl;
        return 0;
    }

