#include <stdio.h>

#define MOD 1000000007

long long factorial(int n) {
    long long res = 1;
    for (int i = 2; i <= n; i++)
        res = (res * i) % MOD;
    return res;
}

long long power(long long base, int exp) {
    long long res = 1;
    base %= MOD;
    while (exp > 0) {
        if (exp % 2 == 1) res = (res * base) % MOD;
        base = (base * base) % MOD;
        exp /= 2;
    }
    return res;
}

long long inverse(long long n) {
    return power(n, MOD - 2);
}

int main() {
    int n;
    scanf("%d", &n);
    long long a[n], b[n];
    for (int i = 0; i < n; i++)
        scanf("%lld %lld", &a[i], &b[i]);

    long long ans = 0;
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            long long total = a[i] + b[i] + a[j] + b[j];
            long long ways = (factorial(total) * inverse(factorial(a[i])) % MOD * inverse(factorial(b[i])) % MOD * inverse(factorial(a[j])) % MOD * inverse(factorial(b[j])) % MOD);
            ans = (ans + ways) % MOD;

        }
    }
    printf("%lld\n", ans);
    return 0;
}
