#include <iostream>
#include <vector>

using namespace std;

long long combinations(int n, int k, const vector<long long>& fact, const vector<long long>& inv) {
  if (k < 0 || k > n) return 0;
  return fact[n] * inv[k] % 1000000007 * inv[n - k] % 1000000007;
}

long long power(long long base, long long exp) {
  long long res = 1;
  base %= 1000000007;
  while (exp > 0) {
    if (exp % 2 == 1) res = (res * base) % 1000000007;
    base = (base * base) % 1000000007;
    exp /= 2;
  }
  return res;
}

long long inverse(long long n) {
  return power(n, 1000000007 - 2);
}

int main() {
  int n;
  cin >> n;

  vector<pair<long long, long long>> packs(n);
  for (int i = 0; i < n; ++i) {
    cin >> packs[i].first >> packs[i].second;
  }

  vector<long long> fact(4002);
  vector<long long> inv(4002);
  fact[0] = 1;
  inv[0] = 1;
  for (int i = 1; i <= 4001; ++i) {
    fact[i] = (fact[i - 1] * i) % 1000000007;
    inv[i] = inverse(fact[i]);
  }

  long long total_ways = 0;
  for (int i = 0; i < n; ++i) {
    for (int j = i + 1; j < n; ++j) {
      long long total_beef = packs[i].first + packs[j].first;
      long long total_pepper = packs[i].second + packs[j].second;
      total_ways = (total_ways + combinations(total_beef + total_pepper, total_beef, fact, inv)) % 1000000007;
    }
  }

  cout << total_ways << endl;
  return 0;
}
