import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long mod = 1000000007;
        long[][] packs = new long[n][2];
        for (int i = 0; i < n; i++) {
            packs[i][0] = sc.nextInt();
            packs[i][1] = sc.nextInt();
        }
        long ans = 0;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                long a = packs[i][0] + packs[j][0];
                long b = packs[i][1] + packs[j][1];
                long ways = 1;
                for (int k = 0; k < a + b; k++) {
                    ways = (ways * (a + b - k)) % mod;
                }
                long inv = power(factorial(a), mod - 2, mod);
                ways = (ways * inv) % mod;
                inv = power(factorial(b), mod - 2, mod);
                ways = (ways * inv) % mod;
                ans = (ans + ways) % mod;
            }
        }
        System.out.println(ans);
    }

    public static long factorial(long n) {
        long res = 1;
        for (long i = 2; i <= n; i++) {
            res = (res * i) % 1000000007;
        }
        return res;
    }

    public static long power(long base, long exp, long mod) {
        long res = 1;
        base %= mod;
        while (exp > 0) {
            if ((exp & 1) == 1) res = (res * base) % mod;
            base = (base * base) % mod;
            exp >>= 1;
        }
        return res;
    }
}
